     method: 'POST',
     headers: { 'Content-Type': 'application/json' },
     changeOrigin: true, 
     body: JSON.stringify(  {
       "name": name,
       "reach": reach,
       "agility": agility,
       "impact": impact,
       "cost": cost,
       "description": description
     },)
   } ;
   fetch('/weapons', requestOptions)
       .then(response => response.json());
       //.then(data => this.setState({ postId: data.id }));
 
     console.log('Save Clicked');
     
