import logo from './logo.svg';
import './App.css';
import React, { Component } from 'react';
import Popup from './components/Popup';
import WeaponList from './components/weaponList'
import {useState} from 'react'
import Table from 'react-bootstrap/Table'
import {Dropdown, Button,  ButtonGroup, DropdownButton, MenuItem } from 'react-bootstrap';

function App() {

const [buttonPopup, setbuttonPopup] =useState(false);

function handleSave(name,reach,agility,impact,cost,description){   

   const requestOptions = {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    changeOrigin: true, 
    body: JSON.stringify(  {
      "name": name,
      "reach": reach,
      "agility": agility,
      "impact": impact,
      "cost": cost,
      "description": description
    },)
  } ;
  fetch('/weapons', requestOptions)
      .then(response => response.json());
      //.then(data => this.setState({ postId: data.id }));

    console.log('Save Clicked');
    //@PostMapping("/weapons")
  } 



  return (
    <div className="App">
        <WeaponList> </WeaponList>

        <button onClick={()=>setbuttonPopup(true)} className='btn btn-secondary btn-sm'>
                  Dodaj
                </button>
    <Popup trigger={buttonPopup}>
    <button className='btn btn-secondary btn-sm' style={{position: "fixed", right: "16px", top: "6px"}} 
    onClick={()=>setbuttonPopup(false)}> 
          Zamknij
        </button>
    <h3> Dodaj nową broń do walki wręcz</h3>
          <form>
            <input type="text" name="weaponName" required="required" placeholder="Nazwa.." />  
            <input type="number" name="weapon" required="required" min="0.5" max="2" step="0.5"placeholder="Nazwa" />  
            <input type="text" name="weaponDescription" placeholder="Opis.." />  


          </form>
        <button className='btn btn-secondary btn-sm' onClick={handleSave("test",1,2,3,4,"test2")}> Zapisz </button>
    </Popup>

    </div>
  );
}

export default App;
