import React from 'react'
import './Popup.css'

function Popup(props) {
  return (props.trigger) ? (
    <div className="popup">
        <div className="popup-inner">
        <button className="btn btn-secondary btn-sm" onClick={()=> props.setTrigger(false)}> 
          Zamknij
        </button>
        {props.children}
              
               
        </div>
  
    </div>
  ) : ""
}

export default Popup
