import React, { Component } from 'react';

class Counter extends Component {
  state = { 
      count: 0,

   } ;

    render() { 
        return (
        <div>
       
            <span className='badge badge-primary m-2'>  // te klasy sa w dokumentacji bootstrap
            {this.state.count}
            </span>
            <button className='btn btn-secondary btn-sm'>
                Increment
             </button>
        </div>);
    }
}
 
export default Counter;
