import React, { Component } from 'react';
import Weapon from './weapon'
import Popup from './Popup';
import Table from 'react-bootstrap/Table'
import {Dropdown, Button,  ButtonGroup, DropdownButton, MenuItem } from 'react-bootstrap';
//import Select from 'react-select'
//import GlobalFiter from './GlobalFilter'
import {useState} from 'react'
class WeaponList extends Component {

  state = {
    data: [],
    add_clicked: 0,
    n_name: 'name_default',
    n_range: 0.5
  }



  constructor(props) {
    super(props);
    this.state = {
      weapons: [],
      n_name: "name_default",
      n_range: 0.5
    };
  }

  componentDidMount() {
    // Simple GET request using fetch
    fetch('http://localhost:8080/weapons')
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            weapons: result
          });
        }
      );
  }

  handleEdit = () => {   // arrow function zeby przekierować "this" w kontrolerze na oryginalny obiekt
    console.log('Edit Clicked');
    

  }
  handleAdd = () => {   // arrow function zeby przekierować "this" w kontrolerze na oryginalny obiekt
    console.log('Add Clicked');
    this.setState({add_clicked: 1});

  }

  handleClose = () => {   // arrow function zeby przekierować "this" w kontrolerze na oryginalny obiekt
    console.log('Close Clicked');
    this.setState({add_clicked: 0});

  }
  handleSave = () => {   
   
    //let test_string = () =>{return this.state.n_name};
    
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      changeOrigin: true, 
      body: JSON.stringify(  {
        "name": this.state.n_name,
        "reach": this.state.n_range,
        "agility": 0.0,
        "impact": 0.0,
        "cost": 0.0,
        "description": ""
      },)
 };

 handleWeponRange= (range) => {   
  this.setState({n_range: range});

}
  fetch('/weapons', requestOptions)
      .then(response => response.json());
      //.then(data => this.setState({ postId: data.id }));

    console.log('Save Clicked');
    //@PostMapping("/weapons")

  }

  render() {
    //const [buttonPopup, setButtonPopup] =useState(false);
    return (
      <div>
        <h2>Broń do walki wręcz</h2>
        <Table >
          <thead>
            <tr>
              <th>Id</th>
              <th>Nazwa</th>
              <th>Zasięg</th>
              <th>Celność</th>
              <th>Impet</th>
              <th>Koszt</th>
              <th>Opis</th>
            </tr>
          </thead>
          <tbody>
            {this.state.weapons.map(w => (
              <tr key={w.id}>
                <td>{w.id}</td>
                <td>{w.name}</td>
                <td>{w.reach}"</td>
                <td>{w.agility}</td>
                <td>{w.impact}</td>
                <td>{w.cost}</td>
                <td>{w.description}</td>
                <button onClick={this.handleEdit} className='btn btn-secondary btn-sm'>
                  Edytuj
                </button>
              </tr>
            ))}
          </tbody>
        </Table>
        
        <Popup trigger={this.state.add_clicked}>
          <div>
            <h3> Dodaj nową broń do walki wręcz</h3>
            <Table >
          <thead>
            <tr>

              <th>Nazwa</th>
              <th>Zasięg</th>
              <th>Celność</th>
              <th>Impet</th>
              <th>Opis</th>
            </tr>
          </thead>
          <tbody>
              <tr >
                <td>
                <form onSubmit={this.handleSubmit}>
                    <input
                      onChange={this.handleChange}
                      value={this.state.n_name}
                    />
                  </form>
                </td>
                <td> 
                <select onChange={this.handleWeponRange(value)} value={this.state.n_range}>
                  <option>0.5</option>
                  <option>1</option>
                  <option>2</option>
                </select>
                </td>
                <td>
                <select>
                  <option>1</option>
                  <option>2</option>
                  <option>3</option>
                </select>
                </td>

                <td>             
                <select>
                  <option>1</option>
                  <option>2</option>
                  <option>3</option>
                </select></td>
                <td>
                  <form onSubmit={this.handleSubmit}>
                    <input
                      onChange={this.handleChange}
                      value={this.state.text}
                    />
                  </form>
                </td>
              </tr>
          </tbody>
        </Table>
            <button className='btn btn-secondary btn-sm' onClick={this.handleClose}> Anuluj </button>
            <button className='btn btn-secondary btn-sm' onClick={this.handleSave}> Zapisz </button>
     
          </div>
           </Popup>


        <button onClick={this.handleAdd} className='btn btn-secondary btn-sm'>
                  Dodaj
                </button>
      </div>
    );
  }

}
export default WeaponList;


