import React, { Component } from 'react';
import Weapon from './weapon'
import Table from 'react-bootstrap/Table'

class WeaponList extends Component {

  state = {
    count: 0,
    data: []
  }

  constructor(props) {
    super(props);
    this.state = {
      weapons: []
    };
  }

  componentDidMount() {
    // Simple GET request using fetch
    fetch('http://localhost:8080/weapons')
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            weapons: result
          });
        }
      );
  }

  handleEdit = () => {   // arrow function zeby przekierować "this" w kontrolerze na oryginalny obiekt
    console.log('Edit Clicked');

  }

  render() {
    return (
      <div>
        <h2>Broń do walki wręcz</h2>
        <Table >
          <thead>
            <tr>
              <th>Id</th>
              <th>Nazwa</th>
              <th>Zasięg</th>
              <th>Celność</th>
              <th>Impet</th>
              <th>Koszt</th>
              <th>Opis</th>
            </tr>
          </thead>
          <tbody>
            {this.state.weapons.map(w => (
              <tr key={w.id}>
                <td>{w.id}</td>
                <td>{w.name}</td>
                <td>{w.reach}</td>
                <td>{w.agility}</td>
                <td>{w.impact}</td>
                <td>{w.cost}</td>
                <td>{w.description}</td>
                <button onClick={this.handleEdit} className='btn btn-secondary btn-sm'>
                  Edytuj
                </button>
              </tr>
            ))}
          </tbody>
        </Table>
      </div>
    );
  }

}
export default WeaponList;


