import React, { Component } from 'react';
import Popup from './Popup';
import Table from 'react-bootstrap/Table';
import BootstrapTable from 'react-bootstrap-table-next';
import 'boostrap/dist/css/boostrap.min.css';
import {Dropdown, Button,  ButtonGroup, DropdownButton, MenuItem } from 'react-bootstrap';
//import Select from 'react-select';
import {useState, useEffect} from 'react';


function WeaponList() {

const [dataList, setdataList] =useState([]);

useEffect(() => {
  fetch('http://localhost:8080/weapons')
  .then(res => res.json())
  .then( (result) => {  setdataList(result)  } )
  .catch(error => console.log(error));
}, [])

const columns = [
  {dataField: 'id', text: 'Id'},
  {dataField: 'name', text: 'Nazwa'},
  {dataField: 'reach', text: 'Zasięg'},
  {dataField: 'agility', text: 'Celność'},
  {dataField: 'impact', text: 'Impet'},
  {dataField: 'cost', text: 'Koszt'},
  {dataField: 'description', text: 'Opis'}

]
  

    return (
      <div>
        <h2>Broń do walki wręcz</h2>

        <BootstrapTable keyField='id' columns={columns} data={dataList} />










     {/*   <Table >
