              <th>Opis</th>
            </tr>
          </thead>
          <tbody>
            {dataList.map(w => (
                 <tr >
                 <td>{w.id}</td>
                 <td>{w.name}</td>
                 <td>{w.reach}"</td>
                 <td>{w.agility}</td>
                 <td>{w.impact}</td>
                 <td>{w.cost}</td>
                 <td>{w.description}</td>
               </tr>
             
            ))}

          </tbody>
            </Table> */}


      </div>
    );
  }


export default WeaponList;


